import React, { Component } from 'react';
import './App.css';
import axios from "axios";
import { Input, Button, Checkbox, message } from "antd";
import Icon from '@ant-design/icons';
import { Form } from '@ant-design/compatible';
import '@ant-design/compatible/assets/index.css';
import loginImg from './login.png'
const FormItem = Form.Item;

class NormalLoginForm extends Component {

  
  
  isLoggedIn = () => {
    if (window.userToken) {
      return true;
    } else {
      return false;
    }
  }

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        var bodyFormData = new FormData()
        bodyFormData.append('username', values.userName)
        bodyFormData.append('password', values.password)
        axios({
          method: "post",
          url: "/login",
          data: bodyFormData,
          headers: { 'Content-Type': 'multipart/form-data' },
        })
          .then(function(response) {
            if (response.data.error) {
              message.error(response.data.message)
            } else if (response.data.error === false){
              message.success('Logged-In!')
              console.log(response.data.data);

              sessionStorage.setItem('token', JSON.stringify(response.data.data.token));
              window.userToken = response.data.data.token;
              console.log(window.userToken);
              setTimeout(()=>{window.location = window.mainAppPage;},500);
            } else {
              message.warning('Unknown response status'+response.data.message)
            }
          })
          .catch(function(response) {
            message.error('Network error. Cannot log in.')
          });
      }
    });
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    if (this.isLoggedIn()) {
      window.location = 'http://localhost:3000/home';
    }
    return (
      <div>
      <div className={this.isLoggedIn() ? ' ' : ' hidden'}>
        Successfully logged in...
      </div>
      <div className={"lContainer"+(this.isLoggedIn() ? ' hidden' : ' ')}>
      <div className="lItem">
          <div className="loginImage">
            <img src={loginImg} width="300" style={{position: 'relative'}} alt="login"/>
          </div>
          <div className="loginForm">
            <h2>Login</h2>
              <Form onSubmit={this.handleSubmit} className="login-form">
              <FormItem>
                {getFieldDecorator("userName", {
                  rules: [{ required: true, message: "Please enter your username" }]
                })(
                  <Input
                    prefix={<Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />}
                    placeholder="Username"
                  />
                )}
              </FormItem>
              <FormItem>
                {getFieldDecorator("password", {
                  rules: [{ required: true, message: "Please enter your Password" }]
                })(
                  <Input
                    prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                    type="password"
                    placeholder="Password"
                  />
                )}
              </FormItem>
              <FormItem>
                {getFieldDecorator("remember", {
                  valuePropName: "checked",
                  initialValue: true
                })(<Checkbox>Remember me</Checkbox>)}
                <Button
                  type="primary"
                  htmlType="submit"
                  className="login-form-button"
                >
                  Log in
                </Button>
              </FormItem>
            </Form>
          </div>
      </div>
      <div className="footer">
        <a href="" target="_blank" rel="noopener noreferrer" className="footerLink">Powered by React</a>
      </div>
      </div>
      </div>
    );
  }
}

const App = Form.create()(NormalLoginForm);

export default App;
